import React from "react";

interface Option {
  id: string;
  label: string;
}

interface Props {
  title: string;
  options: Option[];
  onSelectionChange: (selection: string[]) => void;
  selectedOptions: string[];
  isSlotCategory ?: boolean;
  searchBar?: React.ReactNode;
}

const FilterComp: React.FC<Props> = ({ title, options, onSelectionChange, selectedOptions }) => {
  const handleCheckboxChange = (label: string) => {
    const updatedSelection = selectedOptions.includes(label)
      ? selectedOptions.filter(item => item !== label)
      : [...selectedOptions, label];
    onSelectionChange(updatedSelection);
  };

  return (
    <div className="w-full sm:w-[182px] dark:bg-none p-4 text-center">
      <h6 className="[text-shadow:_0_1px_0_rgb(0_0_0_/_40%)] font-bold mb-2">{title}</h6>
      <div className={`${'isSlotCategory' ? 'grid grid-cols-2 gap-x-2 gap-y-2 sm:gap-x-2 gap-y-2' : ''}`}>
        {options.map((option) => (
          <div key={option.id} className="flex items-center mb-2">
            <input
              id={`checkbox-${option.id}`}
              type="checkbox"
              className="h-4 w-4 border-4 border-blue-300 accent-[#3BF4C7]"
              checked={selectedOptions.includes(option.label)}
              onChange={() => handleCheckboxChange(option.label)}
            />
            <label
              htmlFor={`checkbox-${option.id}`}
              className="ml-2 block text-sm text-black dark:text-[#D5D5D5] [text-shadow:_0_1px_0_rgb(0_0_0_/_40%)]"
            >
              {option.label}
            </label>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FilterComp;

import React from 'react';
import TagContainer from "@/app/components/forumpost/TagContainer";
import CommentField from "@/app/components/forumpost/CommentField";
import CommentContainer from "@/app/components/forumpost/CommentContainer";
import { NumberOfComments } from "@/app/components/forumpost/CommentContainer";
import { VoteButtons } from "@/app/components/common/Buttons";

import { Prisma } from "@prisma/client";
import ForumPostGetPayload = Prisma.ForumPostGetPayload

function ForumPost({ post }: {
  post: ForumPostGetPayload<{
    include: {
      author: {
        select: {
          name: true,
        }
      },
      votes: true,
      tags: true,
      comments: {
        include: {
          author: true,
        }
      },
    }
  }>
}) {
  const userVote = post.votes.length > 0 ? post.votes[0].type : null;

  return (
    <div className="transition-colors w-full h-full p-6 text-black dark:text-[#D5D5D5]">
      <div className="bg-[#82BEE9] dark:bg-[#232530] p-4 md:p-10">
        <h2 className="font-extrabold">{post?.title}</h2>
        <hr className="border-0 h-px my-5 bg-black" />
        <h6>{post?.description}</h6>
        <h6 className="text-right text-s font-normal text-white">Author: {post.author.name?.slice(0, -10)}</h6>
        <br />
        <div className="sm:w-2/3 md:flex md:w-full md:justify-between">
          <div>
            <TagContainer tags={post.tags} />
          </div>
          <div className="hidden md:flex justify-center p-0.5 bg-white dark:bg-[#4F5159]">
            <VoteButtons
              postId={post.id}
              initialUpvotes={post.upvoteCount}
              initialDownvotes={post.downvoteCount}
              initialUserVote={userVote === 'UPVOTE' ? 'up' : userVote === 'DOWNVOTE' ? 'down' : null}
            />
          </div>
        </div>
        <div className="flex md:hidden justify-center p-0.5 bg-white dark:bg-[#4F5159]">
          <VoteButtons
            postId={post.id}
            initialUpvotes={post.upvoteCount}
            initialDownvotes={post.downvoteCount}
            initialUserVote={userVote === 'UPVOTE' ? 'up' : userVote === 'DOWNVOTE' ? 'down' : null}
          />
        </div>

        <br />
        <NumberOfComments commentArray={post.comments} />
        <CommentField forumPostId={post.id} />
        <CommentContainer comments={post.comments} />
      </div>
    </div>
  );
}

export default ForumPost;

"use server";

import { PrismaClient } from "@prisma/client";
import { auth } from "../auth";
import { revalidatePath } from "next/cache";

const prisma = new PrismaClient();

export async function fetchUnclearedItems() {
    const session = await auth();

    // @ts-ignore
    if (session?.user?.role !== "MODERATOR") {
        throw new Error("Access denied");
    }

    const notes = await prisma.note.findMany({
        where: { isClear: false },
        orderBy: { createdAt: "desc" },
    });
    const pastPapers = await prisma.pastPaper.findMany({
        where: { isClear: false },
        orderBy: { createdAt: "desc" },
    });

    const totalUsers = await prisma.user.count();

    return { notes, pastPapers, totalUsers };
}

export async function approveItem(id: string, type: "note" | "pastPaper") {
    const session = await auth();

    // @ts-ignore
    if (session?.user?.role !== "MODERATOR") {
        throw new Error("Access denied");
    }

    type === "note"
        ? await prisma.note.update({ where: { id }, data: { isClear: true } })
        : await prisma.pastPaper.update({
              where: { id },
              data: { isClear: true },
          });

    revalidatePath("/mod");
}

export async function renameItem(
    id: string,
    type: "note" | "pastPaper",
    title: string
) {
    const session = await auth();

    // @ts-ignore
    if (session?.user?.role !== "MODERATOR") {
        throw new Error("Access denied");
    }

    if (type === "note") {
        await prisma.note.update({ where: { id }, data: { title } });
    }

    if (type === "pastPaper") {
        await prisma.pastPaper.update({ where: { id }, data: { title } });
    }

    revalidatePath("/mod");
}

export async function deleteItem(id: string, type: "note" | "pastPaper") {
    const session = await auth();

    // @ts-ignore
    if (session?.user?.role !== "MODERATOR") {
        throw new Error("Access denied");
    }

    if (type === "note") {
        await prisma.note.delete({ where: { id } });
    }

    if (type === "pastPaper") {
        await prisma.pastPaper.delete({ where: { id } });
    }
    revalidatePath("/mod");
}

