import { PrismaClient } from "@prisma/client";
import ForumPost from "./ForumPost";
import { auth } from "@/app/auth";
import {notFound} from "next/navigation";

async function forumPostThread({ params }: { params: { id: string } }) {

  const prisma = new PrismaClient();

  const session = await auth();
  const userId = session?.user?.id;

  const forumpost = await prisma.forumPost.findUnique({
    where: {
      id: params.id,
    },
    include: {
      author: {
        select: {
          name: true,
        }
      },
      votes: {
        where: {
          userId: userId
        }
      },
      tags: true,
      comments: {
        include: {
          author: true,
        }
      },
    },
  });
  if (!forumpost) {
    return notFound();
  }


  if (userId) {
    await prisma.viewHistory.upsert({
      where: { userId_forumPostId: { userId, forumPostId: forumpost.id } },
      update: {
        viewedAt: new Date(),
        count: {
          increment: 1,
        },
      },
      create: {
        userId,
        forumPostId: forumpost.id,
        viewedAt: new Date(),
      },
    })
  }


  return (
    <ForumPost post={forumpost} />
  )
}

export default forumPostThread;

'use server'

import { PrismaClient } from '@prisma/client'
import { auth } from '../auth'
import { revalidatePath } from 'next/cache'

const prisma = new PrismaClient()

type CreateCommentInput = {
  content: string
  forumPostId: string
}
interface NewComment {
  id: string;
  content: string;
  authorId: string;
  forumPostId: string;
  createdAt: Date;
  updatedAt: Date;
}


export async function createComment(data: CreateCommentInput): Promise<{ success: boolean; data?: NewComment; error?: string }> {
  try {
    const session = await auth();

    if (!session || !session.user) {
      throw new Error("Session or user is undefined");
    }
    const newComment = await prisma.comment.create({
      data: {
        content: data.content,
        author: {
          connect: { email: session.user.email! }
        },
        forumPost: {
          connect: { id: data.forumPostId }
        }
      },
    })

    revalidatePath(`/forum/${data.forumPostId}`);
    return { success: true, data: newComment }
  } catch (error) {
    console.error('Server error creating comment:', error)
    return { success: false, error: 'Failed to create comment' }
  }
}

"use client"
import React from 'react';
