import React from "react";
import {auth} from "../../auth";
import {fetchUnclearedItems} from "../../actions/moderatorActions";
import ModeratorDashboardClient from "../../components/ModeratorDashBoard";

async function ModeratorDashboard({searchParams}: {
    searchParams: { page?: string; search?: string; tags?: string | string[] };
}) {
    const session = await auth();

    // @ts-ignore
    if (session?.user?.role !== "MODERATOR") {
        return <div>Access denied. Only moderators can view this page.</div>;
    }

    try {
        const {notes, pastPapers, totalUsers} = await fetchUnclearedItems();
        return (
            <ModeratorDashboardClient
                initialNotes={notes}
                initialPastPapers={pastPapers}
                searchParams={searchParams}
                totalUsers={totalUsers}
            />
        );
    } catch (error) {
        if (error instanceof Error) {
            return <div>Error fetching data: {error.message}</div>;
        } else {
            return <div>Error fetching data: Unknown error occurred.</div>;
        }
    }
}

export default ModeratorDashboard;

import React from 'react';
import Fuse from 'fuse.js';
import {Note, PrismaClient, Tag} from "@prisma/client";
import {redirect} from 'next/navigation';
import Pagination from "../../components/Pagination";
import NotesCard from "../../components/NotesCard";
import SearchBar from "../../components/SearchBar";
import Dropdown from "../../components/FilterComponent";
import UploadButtonNotes from "../../components/UploadButtonNotes";


const SCORE_THRESHOLD = 0.6;

type NoteWithTags = Note & {
    tags: Tag[];
};

function validatePage(page: number, totalPages: number): number {
    if (isNaN(page) || page < 1) {
        return 1;
    }
    if (page > totalPages && totalPages > 0) {
        return totalPages;
    }
    return page;
}

function performSearch(query: string, dataSet: NoteWithTags[]) {
    const options = {
        includeScore: true,
        keys: [
            { name: 'title', weight: 2 },
            { name: 'tags.name', weight: 1 }
        ],
        threshold: 0.6,
        ignoreLocation: true,
        minMatchCharLength: 2,
        findAllMatches: true,
        useExtendedSearch: true,
    };
    const fuse = new Fuse(dataSet, options);
    const searchResults = fuse.search({
        $or: [
            { title: query },
            { 'tags.name': query },
            { title: `'${query}` }
        ]
    });
    return searchResults
        .filter((fuseResult) => (fuseResult.score || 1) < SCORE_THRESHOLD)
        .map((fuseResult) => fuseResult.item);
}

async function notesPage({ searchParams }: { searchParams: { page?: string, search?: string, tags?: string | string[] } }) {
    const prisma = new PrismaClient();
    const pageSize = 9;
    const search = searchParams.search || '';
    const page = parseInt(searchParams.page || '1', 10);
    const tags: string[] = Array.isArray(searchParams.tags)
        ? searchParams.tags
        : (searchParams.tags ? searchParams.tags.split(',') : []);

    let filteredNotes = await prisma.note.findMany({
        where: {
            isClear: true,
            ...(tags.length > 0 && {
                tags: {
                    some: {
                        name: {
                            in: tags,
                        },
                    },
                },
            }),
        },
        include: {
            tags: true,
        },
        orderBy: {
            createdAt: 'desc',
        }
    });
    if (search) {
        filteredNotes = performSearch(search, filteredNotes);
    }

    const totalCount = filteredNotes.length;
    const totalPages = Math.ceil(totalCount / pageSize);

    const validatedPage = validatePage(page, totalPages);

    const startIndex = (validatedPage - 1) * pageSize;
    const endIndex = startIndex + pageSize;
    const paginatedNotes = filteredNotes.slice(startIndex, endIndex);

    if (validatedPage !== page) {
        const searchQuery = search ? `&search=${encodeURIComponent(search)}` : '';
        const tagsQuery = tags.length > 0 ? `&tags=${encodeURIComponent(tags.join(','))}` : '';
        redirect(`/notes?page=${validatedPage}${searchQuery}${tagsQuery}`);
    }

    return (
        <div className="p-8 transition-colors flex flex-col min-h-screen items-center text-black dark:text-[#D5D5D5]">
            <h1 className="text-center mb-4">Notes</h1>
            <div className="hidden w-5/6 lg:w-1/2 md:flex items-center justify-center p-4 space-y-4 sm:space-y-0 sm:space-x-4 pt-2">
                <Dropdown pageType='notes' />
                <SearchBar pageType="notes" initialQuery={search} />
                <UploadButtonNotes />
            </div>

            <div className='flex-col w-5/6 md:hidden space-y-4'>
                <SearchBar pageType="notes" initialQuery={search} />
                <div className='flex justify-between'>
                    <Dropdown pageType='notes' />
                    <UploadButtonNotes />
                </div>
            </div>

            {tags.length > 0 && (
                <div className="flex justify-center mb-4">
                    <div className="flex flex-wrap gap-2">
                        {tags.map((tag, index) => (
                            <span key={index} className="bg-blue-100 text-blue-800 px-2 py-1 rounded">
                                {tag}
                            </span>
                        ))}
                    </div>
                </div>
            )}

            <div className='flex justify-center'>
                <div className="w-full grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-5 p-6  place-content-center">
                    {paginatedNotes.length > 0 ? (
                        paginatedNotes.map((eachNote, index) => (
                            <NotesCard
                                key={eachNote.id}
                                note={eachNote}
                                index={index}
                            />
                        ))
                    ) : (
                        <p className="col-span-3 text-center">
                            {search || tags.length > 0
                                ? "No notes found matching your search or selected tags."
                                : "No notes found."}
                        </p>
                    )}
                </div>
            </div>

            {totalPages > 1 && (
                <div className="mt-auto">
                    <Pagination
                        currentPage={validatedPage}
                        totalPages={totalPages}
                        basePath="/notes"
                        searchQuery={search}
                        tagsQuery={tags.join(',')}
                    />
                </div>
            )}
        </div>
    );
}

export default notesPage;

import React from 'react';
import { PrismaClient, syllabi } from '@prisma/client';
import SyllabusCard from '@/app/components/SyllabusCard';
import SearchBar from "../../components/SearchBar";
import Pagination from "../../components/Pagination";
import Fuse from 'fuse.js';

const prisma = new PrismaClient();
const SCORE_THRESHOLD = 0.6;

function validatePage(page: number, totalPages: number): number {
    if (isNaN(page) || page < 1) {
        return 1;
    }
    if (page > totalPages && totalPages > 0) {
        return totalPages;
    }
    return page;
}

function performSearch(query: string, dataSet: syllabi[]) {
    const options = {
        includeScore: true,
        keys: [
            { name: 'name', weight: 2 },
        ],
        threshold: 0.6,
        ignoreLocation: true,
        minMatchCharLength: 2,
        findAllMatches: true,
        useExtendedSearch: true
    };
    const fuse = new Fuse(dataSet, options);
    const searchResults = fuse.search(query);
    return searchResults
        .filter((fuseResult) => (fuseResult.score || 1) < SCORE_THRESHOLD)
        .map((fuseResult) => fuseResult.item);
}

export default async function SyllabusPage({ searchParams }: { searchParams: { page?: string, search?: string } }) {
    const syllabi = await prisma.syllabi.findMany();
    const pageSize = 16; // We'll keep this constant for server-side pagination
    const search = searchParams.search || '';
    const page = parseInt(searchParams.page || '1', 10);

    let filteredSyllabi = syllabi;
    if (search) {
        filteredSyllabi = performSearch(search, syllabi);
    }

    const totalCount = filteredSyllabi.length;
    const totalPages = Math.ceil(totalCount / pageSize);

    const validatedPage = validatePage(page, totalPages);

    const startIndex = (validatedPage - 1) * pageSize;
    const endIndex = startIndex + pageSize;
    const paginatedSyllabi = filteredSyllabi.slice(startIndex, endIndex);

    return (
        <div className="min-h-screen text-black dark:text-gray-200 flex flex-col items-center justify-start p-8">
            <h1 className="text-center mb-4">Syllabus</h1>

            <div className="w-full max-w-3xl mb-6 sm:mb-8 pt-2">
                <SearchBar pageType="syllabus" initialQuery={search} />
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 sm:gap-6 w-[90vw]">
                {paginatedSyllabi.map((syllabus, index) => (
                    <div key={syllabus.id} className={`
                        ${index < 8 ? '' : 'hidden sm:block'}
                        ${index < 12 ? '' : 'hidden lg:block'}
