import ForumCard from './ForumCard';
import { useRouter } from 'next/navigation';
import { ForumPost, Tag, Comment, PastPaper, Note, Subject, User } from "@prisma/client";

interface ForumPostItem extends Omit<ForumPost, 'upvoteCount' | 'downvoteCount'> {
  type: 'forumpost';
  author?: { name: string | null };
  tags: Tag[];
  comments: (Comment & { author: User })[];
  upvoteCount: number;
  downvoteCount: number;
  votes: { type: 'UPVOTE' | 'DOWNVOTE' }[];
  userVote?: 'UPVOTE' | 'DOWNVOTE' | null;
}


interface PastPaperItem extends Omit<PastPaper, 'type'> {
  type: 'pastpaper';
}

interface NoteItem extends Omit<Note, 'type'> {
  type: 'note';
}

interface SubjectItem extends Omit<Subject, 'type'> {
  type: 'subject';
}

export function mapBookmarkToItem(bookmark: any): Item {
  switch (bookmark.type) {
    case 'forumpost':
      return bookmark as ForumPostItem
    case 'note':
      return bookmark as NoteItem;
    case 'pastpaper':
      return bookmark as PastPaperItem;
    case 'subject':
      return {
        id: bookmark.id,
        name: bookmark.title,
        type: 'subject',
      } as SubjectItem;
    default:
      throw new Error(`Unknown bookmark type: ${(bookmark as any).type}`);
  }
}


type Item = PastPaperItem | NoteItem | SubjectItem | ForumPostItem;


interface FavFetchProps {
  items: Item[];
  activeTab: string;
}

const FavFetch: React.FC<FavFetchProps> = ({ items, activeTab }) => {
  const router = useRouter();
  const [currentTab, setCurrentTab] = useState(activeTab);
  useEffect(() => {
    setCurrentTab(activeTab);
  }, [activeTab]);

  const tabs = ['Past Papers', 'Notes', 'Forum', 'Resources'];

  const handleTabChange = (tab: string) => {
    const url = new URL(window.location.href);
    url.searchParams.set('type', tab);
    url.searchParams.set('page', '1');
    router.push(url.toString());
  };

  const renderContent = () => {
    const filteredItems = items.filter(item => {
      switch (currentTab) {
        case 'Past Papers':
          return item.type === 'pastpaper';
        case 'Notes':
          return item.type === 'note';
        case 'Forum':
          return item.type === 'forumpost';
        case 'Resources':
          return item.type === 'subject';
        default:
          return false;
      }
    });

    if (filteredItems.length === 0) {
      return (
        <div className="flex justify-center items-center h-[calc(65vh-200px)]">
          <p className="text-gray-500">It seems you have not liked anything as of now...</p>
        </div>
      );
    }

    if (currentTab === 'Forum') {
      return (
        <div className="flex flex-col gap-4 pt-6">
          {filteredItems.map((item) => {
            if (item.type === 'forumpost') {
              const forumPostItem = item as ForumPostItem;
              return (
                <ForumCard
                  key={forumPostItem.id}
                  post={forumPostItem}
                  title={forumPostItem.title}
                  desc={forumPostItem.description}
                  author={forumPostItem.author?.name || null}
                  createdAt={forumPostItem.createdAt}
                  tags={forumPostItem.tags}
                  comments={forumPostItem.comments}
                />
              );
            }
            return null;
          })}
        </div>
      );
    }

    return (
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 pt-6">
        {filteredItems.map((item, index) => {
          switch (item.type) {
            case 'pastpaper':
              return (
                <div key={item.id} className="flex justify-center">
                  <PastPaperCard index={index} pastPaper={item} />
                </div>
              );
            case 'note':
              return (
                <div key={item.id} className="flex justify-center">
                  <NotesCard index={index} note={item} />
                </div>
              );
            case 'subject':
              return (
                <div key={item.id} className="flex justify-center">
                  <ResourceCard subject={item} />
                </div>
              );
            default:
              return null;
          }
        })}
      </div>
    );
  };

  return (
    <div className="flex flex-col items-center">
      <div className="flex flex-wrap justify-center w-fit space-x-2 sm:space-x-4 bg-[#82BEE9] dark:bg-[#232530] p-2 sm:p-2 mb-6">
        {tabs.map((tab) => (
          <button
            key={tab}
            onClick={() => handleTabChange(tab)}
            className={`px-1 py-1 sm:px-1 sm:py-1 text-sm sm:text-xs transition-colors duration-200 ${currentTab === tab ? 'bg-[#C2E6EC] dark:bg-[#0C1222] font-semibold' : 'hover:bg-[#ffffff]/10'
              }`}
          >
            {tab}
          </button>
        ))}
      </div>
      <div className="flex justify-center w-svw">
        <div className="w-full md:w-3/4">
          {renderContent()}
        </div>
      </div>
    </div>
  );
}

export default FavFetch;

"use client";

import React, { useState } from "react";
import { Note, PastPaper } from "@prisma/client";
import Pagination from "./Pagination";
import NotesCard from "./NotesCard";
import PastPaperCard from "./PastPaperCard";
import {approveItem, deleteItem, renameItem} from "../actions/moderatorActions";

const PAGE_SIZE = 9;

type NoteWithoutTags = Omit<Note, "tags">;
type PastPaperWithoutTags = Omit<PastPaper, "tags">;

type ModeratorDashboardClientProps = {
    initialNotes: NoteWithoutTags[];
    initialPastPapers: PastPaperWithoutTags[];
    searchParams: { page?: string; search?: string; tags?: string | string[] };
    totalUsers: number;
};

function validatePage(page: number, totalPages: number): number {
    if (isNaN(page) || page < 1) {
        return 1;
    }
    if (page > totalPages && totalPages > 0) {
        return totalPages;
    }
    return page;
}

const ModeratorDashboardClient: React.FC<ModeratorDashboardClientProps> = ({
    initialNotes,
    initialPastPapers,
    searchParams,
    totalUsers,
}) => {
    const [notes, setNotes] = useState<NoteWithoutTags[]>(initialNotes);
    const [pastPapers, setPastPapers] =
        useState<PastPaperWithoutTags[]>(initialPastPapers);
    const [activeTab, setActiveTab] = useState<"notes" | "past_papers">(
        "notes"
    );
    const [selectedItems, setSelectedItems] = useState<string[]>([]);

    const page = parseInt(searchParams.page || "1", 10);

    const items = activeTab === "notes" ? notes : pastPapers;

    const totalCount = items.length;
    const totalPages = Math.ceil(totalCount / PAGE_SIZE);
    const validatedPage = validatePage(page, totalPages);

    const startIndex = (validatedPage - 1) * PAGE_SIZE;
    const endIndex = startIndex + PAGE_SIZE;
    const paginatedItems = items.slice(startIndex, endIndex);

    const handleApprove = async (id: string, type: "note" | "pastPaper") => {
        try {
            await approveItem(id, type);
            if (type === "note") {
                setNotes(notes.filter((note) => note.id !== id));
            } else {
                setPastPapers(pastPapers.filter((paper) => paper.id !== id));
            }
            setSelectedItems(selectedItems.filter((item) => item !== id));
        } catch (error) {
            console.error("Error approving item:", error);
        }
    };

    const handleRename = async (id: string, type: "note" | "pastPaper", newName: string) => {
        try {
            await renameItem(id, type, newName);
            if (type === "note") {
                setNotes(notes.map((note) => note.id === id ? {...note, title: newName} : note));
            } else {
                setPastPapers(pastPapers.map((paper) => paper.id === id ? {...paper, title: newName} : paper));
            }
        } catch (error) {
            console.error("Error renaming item:", error);
        }
    };

    const handleDelete = async (id: string, type: "note" | "pastPaper") => {
        try {
            await deleteItem(id, type);
            type === "note"
                ? setNotes(notes.filter((note) => note.id !== id))
                : setPastPapers(pastPapers.filter((paper) => paper.id !== id));

            setSelectedItems(selectedItems.filter((item) => item !== id));
        } catch (error) {
            console.error("Error deletign item:", error);
        }
    };

    const handleBulkApprove = async () => {
        for (const id of selectedItems) {
            await handleApprove(
                id,
                activeTab === "notes" ? "note" : "pastPaper"
            );
        }
        setSelectedItems([]);
    };

    const handleBulkDelete = async () => {
        for (const id of selectedItems) {
            await handleDelete(
                id,
                activeTab === "notes" ? "note" : "pastPaper"
            );
        }
    };

    const toggleItemSelection = (id: string) => {
        setSelectedItems((prev) =>
            prev.includes(id)
                ? prev.filter((item) => item !== id)
                : [...prev, id]
        );
    };

    return (
        <div className="p-8 transition-colors flex flex-col min-h-screen items-center text-black dark:text-[#D5D5D5]">
            <h1 className="text-center mb-4  font-bold">Moderator Dashboard</h1>
            <h3>Total Users: {totalUsers}</h3>
            <br />
            <div className="w-full flex justify-center mb-6">
                <button
                    className={`mr-2 px-4 py-2 rounded-lg font-semibold transition-colors duration-200 ease-in-out
                        ${
                            activeTab === "notes"
                                ? "bg-blue-500 text-white shadow-md"
                                : "bg-gray-200 text-gray-700 hover:bg-gray-300"
                        }`}
                    onClick={() => setActiveTab("notes")}
                >
                    Notes
                </button>
                <button
                    className={`ml-2 px-4 py-2 rounded-lg font-semibold transition-colors duration-200 ease-in-out
                        ${
                            activeTab === "past_papers"
                                ? "bg-blue-500 text-white shadow-md"
                                : "bg-gray-200 text-gray-700 hover:bg-gray-300"
                        }`}
                    onClick={() => setActiveTab("past_papers")}
                >
                    Past Papers
                </button>
            </div>

            {selectedItems.length > 0 && (
                <button
                    className="mb-4 bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded"
                    onClick={handleBulkApprove}
                >
                    Approve Selected ({selectedItems.length})
                </button>
            )}

            {selectedItems.length > 0 && (
                <button
                    className="mb-4 bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded"
                    onClick={handleBulkDelete}
                >
                    Delete Selected ({selectedItems.length})
                </button>
            )}

            <div className="flex justify-center">
                <div className="w-full grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-5 p-6 place-content-center">
                    {paginatedItems.length > 0 ? (
                        paginatedItems.map((item, index) => (
                            <div key={item.id} className="relative group">
                                <input
                                    type="checkbox"
                                    className="absolute top-2 left-2 z-10"
                                    checked={selectedItems.includes(item.id)}
                                    onChange={() =>
                                        toggleItemSelection(item.id)
                                    }
                                />
                                {activeTab === "notes" ? (
                                    <NotesCard
                                        note={item as NoteWithoutTags}
                                        index={index}
                                        openInNewTab={true}
                                    />
                                ) : (
                                    <PastPaperCard
                                        pastPaper={item as PastPaperWithoutTags}
                                        index={index}
                                        openInNewTab={true}
                                    />
                                )}
                                <div className="flex gap-2 absolute top-2 right-2">
                                    <button
                                        className="bg-green-500 text-white px-3 py-1 rounded-md 
                                                opacity-0 group-hover:opacity-100 transition-opacity duration-200 ease-in-out
                                                hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-opacity-50"
                                        onClick={() =>
                                            handleRename(
                                                item.id,
                                                activeTab === "notes"
                                                    ? "note"
                                                    : "pastPaper",
                                                prompt("Enter new name:") || item.title
                                            )
                                        }
                                    >
                                        Rename
                                    </button>
                                    <button
                                        className="bg-green-500 text-white px-3 py-1 rounded-md
                                                opacity-0 group-hover:opacity-100 transition-opacity duration-200 ease-in-out
                                                hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-opacity-50"
                                        onClick={() =>
                                            handleApprove(
                                                item.id,
                                                activeTab === "notes"
                                                    ? "note"
                                                    : "pastPaper"
                                            )
                                        }
                                    >
                                        Approve
                                    </button>
                                    <button
                                        className="bg-red-500 text-white px-3 py-1 rounded-md 
                                                opacity-0 group-hover:opacity-100 transition-opacity duration-200 ease-in-out
                                                hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-opacity-50"
                                        onClick={() =>
                                            handleDelete(
                                                item.id,
                                                activeTab === "notes"
                                                    ? "note"
                                                    : "pastPaper"
                                            )
                                        }
                                    >
                                        Delete
                                    </button>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p className="col-span-3 text-center text-gray-500 italic">
                            No {activeTab === "notes" ? "notes" : "past papers"}{" "}
                            found.
                        </p>
                    )}
                </div>
            </div>

            {totalPages > 1 && (
                <div className="mt-auto">
                    <Pagination
                        currentPage={validatedPage}
                        totalPages={totalPages}
                        basePath="/mod"
                    />
                </div>
            )}
        </div>
    );
};

export default ModeratorDashboardClient;

"use client";

import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
