"use client"

// Inspired by react-hot-toast library
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

"use client";
import React, { useState, useEffect, useRef } from "react";
import NavBar from "@/app/components/NavBar";
import Header from "@/app/components/header";
import Image from "next/image";
import HomeFooter from "@/app/@protected_routes/home/home_footer";

export default function ClientSide({
    children,
}: {
    children: React.ReactNode;
}) {
    const [isNavOn, setIsNavOn] = useState(false);
    const [darkMode, setDarkMode] = useState(false);
    const navbarRef = useRef<HTMLDivElement | null>(null);

    useEffect(() => {
        const savedNavState = localStorage.getItem("isNavOn");
        const savedDarkMode = localStorage.getItem("darkMode");
        if (savedNavState !== null) {
            setIsNavOn(savedNavState === "true");
        }
        if (savedDarkMode !== null) {
            setDarkMode(savedDarkMode === "true");
        }
    }, []);

    const toggleNavbar = () => {
        setIsNavOn((prevState) => {
            const newState = !prevState;
            localStorage.setItem("isNavOn", newState.toString());
            return newState;
        });
    };

    const toggleTheme = () => {
        setDarkMode((prevState) => {
            const newState = !prevState;
            localStorage.setItem("darkMode", newState.toString());
            return newState;
        });
    };

    const handleClickOutside = (event: MouseEvent) => {
        if (
            navbarRef.current &&
            !navbarRef.current.contains(event.target as Node)
        ) {
            setIsNavOn(false);
            localStorage.setItem("isNavOn", "false");
        }
    };

    useEffect(() => {
        if (isNavOn) {
            document.addEventListener("mousedown", handleClickOutside);
        } else {
            document.removeEventListener("mousedown", handleClickOutside);
        }

        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [isNavOn]);

    return (
        <div className={`relative flex`}>
            {!isNavOn && (
                <button
                    title="Open Navbar"
                    onClick={toggleNavbar}
                    className="absolute top-4 left-4 z-50 opacity-100"
                >
                    <Image
                        src="/assets/HamburgerIcon.svg"
                        alt="Menu"
                        width={30}
                        height={30}
                        className="dark:invert-[.835] transition-transform transform-gpu hover:scale-110 hover:-translate-y-1"
                    />
                </button>
            )}
            <div ref={navbarRef}>
                <NavBar isNavOn={isNavOn} toggleNavbar={toggleNavbar} />
            </div>
            {isNavOn && (
                <div className="fixed inset-0 bg-black bg-opacity-50 z-10 transition-opacity duration-300"></div>
            )}
            <main
                className={`flex-grow transition-all duration-300 ease-in-out ${
                    isNavOn ? "lg:w-[95vw] md:w-[92vw]" : "w-[100vw]"
                }`}
            >
                <div
                    className={`min-h-screen flex flex-col justify-between ${
                        darkMode ? "dark" : ""
                    }`}
                >
                    <Header toggleTheme={toggleTheme} darkMode={darkMode} />
                    {children}
                    <HomeFooter />
                </div>
            </main>
        </div>
    );
}

import React from "react";
import {auth} from "@/app/auth";
import {SessionProvider} from "next-auth/react";
import ClientSide from "./clientSide";
import {PrismaClient} from "@prisma/client";
import BookmarksProvider from "@/app/components/BookmarksProvider";

export default async function Layout({
                                         children,
                                     }: Readonly<{
    children: React.ReactNode;
}>) {
    const prisma = new PrismaClient();

    const session = (await auth())!;

    const user = await prisma.user.findUniqueOrThrow({
        where: {email: session.user!.email!},
        include: {
            bookmarkedNotes: true,
            bookmarkedPastPapers: true,
            bookmarkedForumPosts: {
                include: {
                    votes: true,
                    author: true,
                    tags: true,
                    comments: {
                        include: {
                            author: true,
                        },
                    },
                },
            },
            bookmarkedResources: true,
        },
    });

    const initialBookmarks = [
        ...user.bookmarkedNotes.map((note) => ({
            id: note.id,
            type: "note" as const,
            title: note.title,
            thumbNailUrl: note.thumbNailUrl,
        })),
        ...user.bookmarkedPastPapers.map((paper) => ({
            id: paper.id,
            type: "pastpaper" as const,
            title: paper.title,
            thumbNailUrl: paper.thumbNailUrl,
        })),
        ...user.bookmarkedForumPosts.map((post) => ({
            id: post.id,
            type: "forumpost" as const,
            title: post.title,
            upvoteCount: post.upvoteCount,
            createdAt: post.createdAt,
            downvoteCount: post.downvoteCount,
            votes: post.votes.map((vote) => ({type: vote.type})),
            author: post.author ? {name: post.author.name} : undefined,
            tags: post.tags,
            comments: post.comments.map((comment) => ({
                ...comment,
                author: comment.author
                    ? {name: comment.author.name}
                    : undefined,
            })),
        })),
        ...user.bookmarkedResources.map((resource) => ({
            id: resource.id,
            type: "subject" as const,
            title: resource.name,
        })),
    ];

    return (
        <SessionProvider>
            <ClientSide>
                <BookmarksProvider initialBookmarks={initialBookmarks}>
                    {children}
                </BookmarksProvider>
            </ClientSide>
        </SessionProvider>
    );
}

import React from 'react';
import Fuse from 'fuse.js';
import { PrismaClient, Subject } from '@prisma/client';
import { redirect } from 'next/navigation';
import ResourceCard from '../../components/ResourceCard';
import Pagination from '../../components/Pagination';
import SearchBar from '../../components/SearchBar';

const SCORE_THRESHOLD = 0.8;
const prisma = new PrismaClient();

function validatePage(page: number, totalPages: number): number {
    if (isNaN(page) || page < 1) {
        return 1;
    }
    if (page > totalPages && totalPages > 0) {
        return totalPages;
    }
    return page;
}

function performSearch(query: string, dataSet: Subject[]) {
    const options = {
        includeScore: true,
        keys: ['name'],
        threshold: 0.6,
        ignoreLocation: true,
        minMatchCharLength: 2,
    };
    const fuse = new Fuse(dataSet, options);
    const searchResults = fuse.search(query);
    return searchResults
        .filter((fuseResult) => (fuseResult.score || 1) < SCORE_THRESHOLD)
        .map((fuseResult) => fuseResult.item);
}

export default async function ResourcesPage({ searchParams }: { searchParams: { page?: string, search?: string } }) {
    const pageSize = 12;
    const search = searchParams.search || '';
    const page = parseInt(searchParams.page || '1', 10);

    const allSubjects = await prisma.subject.findMany();

    let filteredSubjects = allSubjects;
    if (search) {
        filteredSubjects = performSearch(search, allSubjects);
    }

    const totalCount = filteredSubjects.length;
    const totalPages = Math.ceil(totalCount / pageSize);

    const validatedPage = validatePage(page, totalPages);

    const startIndex = (validatedPage - 1) * pageSize;
    const endIndex = startIndex + pageSize;
    const paginatedSubjects = filteredSubjects.slice(startIndex, endIndex);

    if (validatedPage !== page) {
        redirect(`/resources?page=${validatedPage}${search ? `&search=${encodeURIComponent(search)}` : ''}`);
    }

    return (
        <div className="transition-colors min-h-screen text-black dark:text-gray-200">
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-8">
                <h1 className="text-center mb-8">Resource Repo</h1>

                <div className="max-w-3xl mx-auto mb-8">
                    <SearchBar pageType="resources" initialQuery={search} />
                </div>

                {paginatedSubjects.length > 0 ? (
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                        {paginatedSubjects.map((subject) => (
                            <ResourceCard key={subject.id} subject={subject} />
                        ))}
                    </div>
                ) : (
                    <p className="text-center text-lg">
                        {search
                            ? "No subjects found matching your search."
                            : "No subjects found."}
                    </p>
                )}

                {totalPages > 1 && (
                    <div className="mt-12">
                        <Pagination
                            currentPage={validatedPage}
                            totalPages={totalPages}
                            basePath="/resources"
                            searchQuery={search}
                        />
                    </div>
                )}
            </div>
        </div>
    );
}

