'use client'

import React, { useState, useEffect } from 'react'
import { usePathname, useRouter } from 'next/navigation'
import { ChevronRight, Clock, Trophy, Target, ChevronDown, ChevronUp, ArrowLeft, RotateCcw } from 'lucide-react'

import { WildlifeJSON } from "@/public/assets/quizJSON"
import { ForestJSON } from "@/public/assets/quizJSON"
import { spokenenglishJSON } from "@/public/assets/quizJSON"

interface Question {
    question: string
    options: string[]
    answer: string
}

interface QuizQuestion extends Question {
    selectedAnswer?: string
    isMarked?: boolean
    weekNumber: string
    isExpanded?: boolean
    originalIndex?: number  //tracks original index
}

interface Week {
    name: string
    questions?: Question[]
    content?: Question[]
}

interface CourseData {
    title: string
    code: string
    weeks: Week[]
}

const getCourseData = (courseCode: string): CourseData => {
    switch (courseCode) {
        case "102104073":
            return { ...WildlifeJSON, code: courseCode } as CourseData
        case "102104082":
            return { ...ForestJSON, code: courseCode } as CourseData
        case "109106067":
            return { ...spokenenglishJSON, code: courseCode } as CourseData
        default:
            return { ...WildlifeJSON, code: "102104073" } as CourseData
    }
}

export default function Component() {
    const pathname = usePathname()
    const router = useRouter()

    const [questions, setQuestions] = useState<QuizQuestion[]>([])
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0)
    const [timeRemaining, setTimeRemaining] = useState(0)
    const [quizSubmitted, setQuizSubmitted] = useState(false)
    const [score, setScore] = useState(0)
    const [showWarning, setShowWarning] = useState(false)
    const [showOnlyIncorrect, setShowOnlyIncorrect] = useState(false)
    const [expandedQuestionIndex, setExpandedQuestionIndex] = useState<number | null>(null)
    const [showError, setShowError] = useState(false)

    useEffect(() => {
        const params = new URLSearchParams(pathname.split("quiz/")[1])
        const weeks = params.get("weeks")?.split("-") || []
        const numQuestions = parseInt(params.get("numQ") || "0")
        const time = params.get("time") || "000000"
        const courseCode = params.get("course") || "102104073"

        const hours = parseInt(time.slice(0, 2))
        const minutes = parseInt(time.slice(2, 4))
        const seconds = parseInt(time.slice(4, 6))
        const totalSeconds = hours * 3600 + minutes * 60 + seconds
        setTimeRemaining(totalSeconds)

        const courseData = getCourseData(courseCode)

        const allQuestions: QuizQuestion[] = weeks.flatMap((week) => {
            const weekData = courseData.weeks.find((w) => w.name === week)

            if (!weekData) {
                console.warn(`Week ${week} not found in course data`)
                return []
            }

            const questionsArray = weekData.questions || weekData.content || []

            return questionsArray.map((q: Question, index) => ({
                ...q,
                weekNumber: week,
                isExpanded: false,
                originalIndex: index  // Store original index of q
            }))
        })

        const shuffledQuestions = allQuestions.sort(() => Math.random() - 0.5)
        const selectedQuestions = shuffledQuestions.slice(0, numQuestions).map((q, index) => ({
            ...q,
            originalIndex: index
        }))
        setQuestions(selectedQuestions)
    }, [pathname])

    useEffect(() => {
        if (timeRemaining > 0 && !quizSubmitted) {
            const timer = setInterval(() => {
                setTimeRemaining((prev) => {
                    if (prev <= 1) {
                        submitQuiz()
                        clearInterval(timer)
                        return 0
                    }

                    if (prev === 30) {
                        setShowWarning(true)
                    }
                    return prev - 1
                })
            }, 1000)

            return () => clearInterval(timer)
        }
    }, [timeRemaining, quizSubmitted])

    const formatTime = (totalSeconds: number) => {
        const hours = Math.floor(totalSeconds / 3600)
        const minutes = Math.floor((totalSeconds % 3600) / 60)
        const seconds = totalSeconds % 60
        return `${String(hours).padStart(2, "0")}:${String(minutes).padStart(2, "0")}:${String(seconds).padStart(2, "0")}`
    }

    const handleAnswerSelect = (answer: string) => {
        setShowError(false)
        const updatedQuestions = [...questions]
        updatedQuestions[currentQuestionIndex].selectedAnswer = answer
        setQuestions(updatedQuestions)
    }

    const isAnswerCorrect = (selected: string, correctAnswers: string | string[]) => {
        const normalizedSelected = selected.trim().toLowerCase();
        if (typeof correctAnswers === 'string') {
            return normalizedSelected === correctAnswers.trim().toLowerCase();
        }
        if (Array.isArray(correctAnswers)) {
            return correctAnswers.some(answer => normalizedSelected === answer.trim().toLowerCase());
        }
        return false;
    }

    const submitQuiz = () => {
        const correctAnswersCount = questions.filter((q) => {
            const selectedAnswer = (q.selectedAnswer || "").trim();
            return isAnswerCorrect(selectedAnswer, q.answer);
        }).length;
        setScore(correctAnswersCount);
        setQuizSubmitted(true);
    }

    const goToNextQuestion = () => {
        const currentQuestion = questions[currentQuestionIndex]

        if (!currentQuestion.selectedAnswer) {
            setShowError(true)
            return
        }

        setShowError(false)
        if (currentQuestionIndex === questions.length - 1) {
            submitQuiz()
        } else {
            setCurrentQuestionIndex(currentQuestionIndex + 1)
        }
    }

    const toggleQuestionExpansion = (index: number) => {
        if (quizSubmitted) {
            setExpandedQuestionIndex(expandedQuestionIndex === index ? null : index)
        }
    }

    const getScoreColor = (percentage: number) => {
        if (percentage >= 80) return "text-green-400 dark:bg-green-800/20"
        if (percentage >= 60) return "text-yellow-400 dark:bg-yellow-800/20"
        return "dark:bg-red-800/20 text-red-400"
    }

    if (questions.length === 0) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="animate-spin h-16 w-16 border-t-2 border-b-2 border-gray-900"></div>
            </div>
        )
    }

    if (quizSubmitted) {
        const displayedQuestions = showOnlyIncorrect
            ? questions.filter((q) => !isAnswerCorrect(q.selectedAnswer || "", q.answer))
            : questions

        const percentage = ((score / questions.length) * 100).toFixed(1)

        return (
            <div className="lg:w-[75vw] md:w-[90vw] mx-auto px-4 py-8">
                <div className="mb-8 bg-[#5FC4E7] dark:bg-[#ffffff]/20 shadow-lg overflow-hidden dark:border-2">
                    <div className="text-center p-6">
                        <div className="flex justify-center mb-4">
                            <Trophy className="w-16 h-16 dark:text-[#D5D5D5]" />
                        </div>
                        <h1 className="text-2xl font-bold dark:text-[#D5D5D5]">Quiz Complete!</h1>
                        <p className="text-lg mt-2 text-black dark:text-[#D5D5D5] font-semibold">
                            Here's how you performed
                        </p>
                    </div>
                    <div className="p-6">
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-center p-2">
                            <div className={`text-4xl font-bold flex flex-col justify-center items-center p-4 ${getScoreColor(Number(percentage))}`}>
                                <p className="text-md uppercase font-medium mb-1">Score</p>
                                <p className="text-3xl">{score}/{questions.length}</p>
                            </div>
                            <div className={`text-4xl font-bold flex flex-col justify-center items-center p-4 ${getScoreColor(Number(percentage))}`}>
                                <p className="text-md uppercase font-medium mb-1">Percentage</p>
                                <p className="text-3xl font-bold">{percentage}%</p>
                            </div>
                            <div className={`text-4xl font-bold flex flex-col justify-center items-center p-4 ${getScoreColor(Number(percentage))}`}>
                                <p className="text-md uppercase font-medium mb-1">Questions</p>
                                <p className="text-3xl">{questions.filter((q) => isAnswerCorrect(q.selectedAnswer || "", q.answer)).length} correct</p>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="mb-6 flex items-center justify-between">
                    <div className="flex items-center space-x-2 bg-[#5FC4E7] dark:bg-[#ffffff]/20 p-3">
                        <Target size={20} />
                        <label className="flex items-center space-x-2 cursor-pointer">
                            <input
                                type="checkbox"
                                checked={showOnlyIncorrect}
                                onChange={(e) => setShowOnlyIncorrect(e.target.checked)}
                                className="form-checkbox h-5 w-5"
                            />
                            <span className="text-base font-medium dark:text-[#D5D5D5]">
                                Show Incorrect Only
                            </span>
                        </label>
                    </div>
                    <span className="text-md text-dark font-medium dark:text-[#D5D5D5] p-4 justify-end">
                        Showing {displayedQuestions.length} of {questions.length} questions
                    </span>
                </div>

                <div className="w-full grid sm:grid-cols-4 grid-cols-3 gap-4 mb-6">
                    {displayedQuestions.map((q, index) => (
                        <div
                            key={index}
                            className={`p-2 py-4 cursor-pointer transition-all duration-300 ${expandedQuestionIndex === index ? "col-span-4" : ""
                                } ${isAnswerCorrect(q.selectedAnswer || "", q.answer)
                                    ? "bg-green-200 dark:bg-[#1a271a] text-[#037d00]"
                                    : "bg-red-200 dark:bg-[#341a1a] font-semibold text-[#cb0909]"
                                }`}
                            onClick={() => toggleQuestionExpansion(index)}
                        >
                            <div className="flex justify-between items-center">
                                <div className="flex flex-col">
                                    <p className="text-md text-black dark:text-[#D5D5D5]">Question {(q.originalIndex || 0) + 1}</p>
                                    <p className="text-sm text-black dark:text-[#D5D5D5] opacity-75">Week {q.weekNumber}</p>
                                </div>
                                {expandedQuestionIndex === index ? <ChevronUp size={20} /> : <ChevronDown size={20} />}
                            </div>
                            {expandedQuestionIndex === index && (
                                <div className="mt-2">
                                    <p className="text-black dark:text-[#D5D5D5]">{q.question}</p>
                                    <p className="mt-2">
                                        Your answer:{" "}
                                        <span className={isAnswerCorrect(q.selectedAnswer || "", q.answer) ? "text-green-800 font-semibold" : "text-red-800 font-semibold"}>
                                            {q.selectedAnswer || "Not answered"}
                                        </span>
                                    </p>
                                    <p className="mt-1 text-green-800 font-semibold">Correct answer: {q.answer}</p>
                                </div>
                            )}
                        </div>
                    ))}
                </div>

                <div className="mt-8 flex flex-col gap-4 sm:flex-row items-center justify-between font-semibold dark:text-[#D5D5D5]">
                    <div className='flex gap-4 justify-between sm:justify-start w-full sm:w-fit'>
                        <button
                            onClick={() => router.push("/quiz")}
                            className="flex items-center px-6 py-3 bg-[#5FC4E7] dark:bg-[#008A90] hover:opacity-90 transition-opacity"
                        >
                            <ArrowLeft size={20} className="mr-2" />
                            Try Another Quiz
                        </button>
                        <button
                            className="flex items-center px-6 py-3 bg-[#5FC4E7] dark:bg-[#008A90] hover:opacity-90 transition-opacity"
                            onClick={() =>  window.location.reload()}
                        >
                            <RotateCcw size={20} className='mr-2' />
                            Retry Quiz
                        </button>
                    </div>
                    <button
                        onClick={() => router.push("/")}
                        className="flex items-center justify-center w-full sm:w-fit px-6 py-3 bg-[#5FC4E7] dark:bg-[#008A90] hover:opacity-90 transition-opacity"
                    >
                        Return Home
                        <ChevronRight size={20} className="ml-2" />
                    </button>
                </div>
            </div>
        )
    }

    const currentQuestion = questions[currentQuestionIndex]

    return (
        <div className="mx-auto px-4 sm:px-6 lg:px-8 py-4 sm:py-6">
            <div className="flex justify-between items-center mb-4 sm:mb-6">
                <div className="flex items-center space-x-2">
                    <Clock
                        className={`${timeRemaining <= 30 ? "text-red-500 animate-pulse" : "text-black dark:text-white"
                            }`}
                    />
                    <span
                        className={`font-mono text-lg sm:text-xl ${timeRemaining <= 30 ? "text-red-500" : "text-black dark:text-white"
                            }`}
                    >
                        {formatTime(timeRemaining)}
                    </span>
                </div>
                <div className="text-lg sm:text-md text-black dark:text-white">
                    Question {currentQuestionIndex + 1} of {questions.length}
                </div>
            </div>

            {showWarning && (
                <div className="bg-red-50 border border-red-200 text-red-700 px-3 py-2 sm:px-4 sm:py-3 rounded-lg mb-4 text-sm sm:text-base">
                    30 seconds remaining! Please finish your quiz.
                </div>
            )}

            <div className="flex flex-col items-center justify-center">
                <div className="flex mb-4 bg-[#5FC4E7] dark:bg-[#008A90] text-black dark:text-[#D5D5D5] min-h-[10vh] w-[70vw] shadow-md">
                    <h2 className="text-base sm:text-xl font-medium flex justify-center items-center p-3 sm:p-4 text-center shadow-sm">
                        {currentQuestionIndex + 1}. {currentQuestion.question}
                    </h2>
                </div>

                <div className="space-y-3 w-[60vw]">
                    {currentQuestion.options.map((option, index) => (
                        <button
                            key={index}
                            onClick={() => handleAnswerSelect(option)}
                            className={`p-3 sm:p-4 text-left dark:border dark:border-[#D5D5D5] transition-colors w-full text-sm sm:text-base text-black dark:text-[#D5D5D5] ${currentQuestion.selectedAnswer === option
                                ? "bg-[#82BEE9] dark:bg-white/20 text-white shadow-lg"
                                : "bg-[#5FC4E7] dark:bg-[#0C1222]"
                                }`}
                        >
                            {option}
                        </button>
                    ))}
                </div>

                {showError && (
                    <div className="mt-4 p-3 bg-red-50 border border-red-200 text-red-600 rounded-lg text-sm w-[60vw] text-center">
                        Please select an answer before proceeding
                    </div>
                )}
            </div>

            <div className="flex justify-end space-x-4 mt-4 sm:mt-6">
                <button
                    onClick={goToNextQuestion}
                    className="dark:text-[#D5D5D5] px-4 sm:px-6 py-2 text-lg font-semibold bg-[#5FC4E7] dark:bg-[#008A90] hover:opacity-90 transition-opacity"
                >
                    {currentQuestionIndex === questions.length - 1 ? "Submit" : "Next"}
                    <ChevronRight size={20} className="ml-2 inline" />
                </button>
            </div>
        </div>
    )
}

import React from 'react';
import { PrismaClient } from '@prisma/client';
import PDFViewer from '@/app/components/pdfviewer';
import { auth } from '@/app/auth';
import { notFound } from "next/navigation";
import DeleteButton from '@/app/components/DeleteButton';

const prisma = new PrismaClient();

async function getSyllabus(id: string) {
    return prisma.syllabi.findUnique({
        where: { id }
    });
}

function processSyllabusName(input: string): string {
    return input
      .slice(9) 
      .replace(/\.pdf$/, '') 
      .replace(/_/g, ' '); 
  }

async function SyllabusViewerPage({ params }: { params: { id: string } }) {
    const prisma = new PrismaClient();
    let syllabus;
    const session = await auth();
    const userId = session?.user?.id;

    try {
        syllabus = await getSyllabus(params.id);

        if (userId && syllabus?.id) {
            await prisma.viewHistory.upsert({
                where: {
                    userId_syllabusId: { userId, syllabusId: syllabus.id}
                },
                update: {
                    viewedAt: new Date(),
                    count: {
                        increment: 1,
                    },
                },
                create: {
                    userId,
                    syllabusId: syllabus.id,
                    viewedAt: new Date(),
                    count: 1,
                },
            });
        }
    } catch (error) {
        console.error('Error fetching syllabus:', error);
        return (
            <div>
                <div className="text-center p-8 dark:text-[#d5d5d5]">
                    Error loading syllabus. Please refresh, or try again later.
                </div>
            </div>
        );
    } finally {
        await prisma.$disconnect();
    }

    if (!syllabus) {
        return notFound();
    }

    //const postTime: string = syllabus.createdAt.toLocaleString("en-US", { timeZone: "Asia/Kolkata" });

    return (
        <div className="flex flex-col lg:flex-row h-screen text-black dark:text-[#D5D5D5]">
            <div className="lg:w-1/2 flex flex-col overflow-hidden">
                <div className="flex-grow overflow-y-auto p-4 sm:p-6 lg:p-8">
                    <div className="max-w-2xl mx-auto">
                        <h1 className="text-2xl sm:text-3xl lg:text-4xl font-bold mb-4 sm:mb-6">{processSyllabusName(syllabus.name)}</h1>
                        <div className="space-y-2 sm:space-y-3">
                            <div className="flex gap-2 items-center">
                                {/* {syllabus.author?.id === userId &&
                                    <DeleteButton itemID={syllabus.id} activeTab='syllabi'/>
                                } */}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="flex-1 lg:w-1/2 overflow-hidden lg:border-l lg:border-black dark:lg:border-[#D5D5D5] p-4">
                <div className="h-full overflow-auto">
                    <PDFViewer fileUrl={syllabus.fileUrl} />
                </div>
            </div>
        </div>
    );
}

export default SyllabusViewerPage;

'use server'

import { PrismaClient } from '@prisma/client';
import { auth } from "@/app/auth";

const prisma = new PrismaClient()

export async function upvotePost(postId: string) {
    const session = await auth();
    const userId = session?.user?.id;
    if (!userId) {
        return
    }
    try {
        const existingVote = await prisma.vote.findUnique({
            where: {
                userId_forumPostId: {
                    userId,
                    forumPostId: postId,
                },
            },
        });

        if (existingVote) {
            if (existingVote.type === 'UPVOTE') {
                await prisma.vote.delete({
                    where: {
                        id: existingVote.id,
                    },
                });
                await prisma.forumPost.update({
                    where: { id: postId },
                    data: { upvoteCount: { decrement: 1 } },
                });
            } else {
                // Change downvote to upvote
                await prisma.vote.update({
                    where: { id: existingVote.id },
                    data: { type: 'UPVOTE' },
                });
                await prisma.forumPost.update({
                    where: { id: postId },
                    data: {
                        upvoteCount: { increment: 1 },
                        downvoteCount: { decrement: 1 },
                    },
                });
            }
        } else {
            await prisma.vote.create({
                data: {
                    userId,
                    forumPostId: postId,
                    type: 'UPVOTE',
                },
            });
            await prisma.forumPost.update({
                where: { id: postId },
                data: { upvoteCount: { increment: 1 } },
            });
        }

        const updatedPost = await prisma.forumPost.findUnique({
            where: { id: postId },
            select: { upvoteCount: true },
        });

        return { success: true, upvoteCount: updatedPost?.upvoteCount };
    } catch (error) {
        console.error('Error upvoting post:', error);
        return { success: false, error: 'Failed to upvote post' };
    }
}



export async function downvotePost(postId: string) {
    try {
        const session = await auth();
        const userId = session?.user?.id;
        if (!userId) {
            return
        }
        const existingVote = await prisma.vote.findUnique({
            where: {
                userId_forumPostId: {
                    userId,
                    forumPostId: postId,
                },
            },
        });

        if (existingVote) {
            if (existingVote.type === 'DOWNVOTE') {
                await prisma.vote.delete({
                    where: {
                        id: existingVote.id,
                    },
                });
                await prisma.forumPost.update({
                    where: { id: postId },
                    data: { downvoteCount: { decrement: 1 } },
                });
            } else {
                await prisma.vote.update({
                    where: { id: existingVote.id },
                    data: { type: 'DOWNVOTE' },
                });
                await prisma.forumPost.update({
                    where: { id: postId },
                    data: {
                        upvoteCount: { decrement: 1 },
                        downvoteCount: { increment: 1 },
                    },
                });
            }
        } else {
            // Add a new downvote
            await prisma.vote.create({
                data: {
                    userId,
                    forumPostId: postId,
                    type: 'DOWNVOTE',
                },
            });
            await prisma.forumPost.update({
                where: { id: postId },
                data: { downvoteCount: { increment: 1 } },
            });
        }

        const updatedPost = await prisma.forumPost.findUnique({
            where: { id: postId },
            select: { downvoteCount: true },
        });

        return { success: true, downvoteCount: updatedPost?.downvoteCount };
    } catch (error) {
        console.error('Error downvoting post:', error);
        return { success: false, error: 'Failed to downvote post' };
    }

}

"use client";

import { useEffect, useState } from "react";
import Image from "next/image";

function ThemeToggleSwitch() {
    const [mounted, setMounted] = useState(false);
    const [darkMode, setDarkMode] = useState(true)

    useEffect(() => {
        const theme = localStorage.getItem('theme');
        const isDarkMode = theme === 'dark' || (theme === null && window.matchMedia('(prefers-color-scheme: dark)').matches);
        setDarkMode(isDarkMode);
        if (isDarkMode) {
            document.documentElement.classList.add('dark');
        } else {
            document.documentElement.classList.remove('dark');
        }
        setMounted(true);
    }, []);

    useEffect(() => {
        if (mounted) {
            if (darkMode) {
                document.documentElement.classList.add('dark');
                localStorage.setItem('theme', 'dark');
            } else {
                document.documentElement.classList.remove('dark');
                localStorage.setItem('theme', 'light');
            }
        }
    }, [darkMode, mounted]);

    const toggleTheme = () => setDarkMode(!darkMode);

    if (!mounted) {
        return null;
    }

    return (
        <div onClick={toggleTheme} className="relative overflow-hidden flex items-center justify-center w-10 h-6 dark:bg-[#121B31] border-[1px] border-[#2699E9] dark:border-[#D5D5D5] rounded-full hover:border-1 hover:border-[#3BF4C7] dark:hover:border-[#3BF4C7] transition-all ease-linear  duration-150 cursor-pointer">
            <div className="flex justify-center items-center bg-[#D0E9ED] dark:bg-[#0C1120] shadow-md w-4 h-4 rounded-full -translate-x-2 dark:translate-x-2 transition-transform ease-in-out">
                <Image src={"/assets/LucideSun.svg"} alt="Sun" width={25} height={25} className={`${darkMode ? "hidden" : ""}`} />
                <Image src={"/assets/LucideMoon.svg"} alt="Moon" width={25} height={25} className={`invert-[.835] ${!darkMode ? "hidden" : ""}`} />
            </div>
        </div>
    )}

export default ThemeToggleSwitch;

"use client";

import React, {
    useState,
    useEffect,
    useRef,
    useMemo,
    useTransition,
} from "react";
import Link from "next/link";
import { createForumPost } from "../actions/CreateForumPost";
import { useRouter } from "next/navigation";
import Fuse from "fuse.js";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowLeft, faSquareXmark } from "@fortawesome/free-solid-svg-icons";
import { useToast } from "@/components/ui/use-toast";
import Loading from "@/app/@protected_routes/loading";

const years = ["2020", "2021", "2022", "2023", "2024"];
const slots = [
    "A1",
    "A2",
    "B1",
    "B2",
    "C1",
    "C2",
    "D1",
    "D2",
    "E1",
    "E2",
    "F1",
    "F2",
    "G1",
    "G2",
];

const filterYearAndSlot = (tags: string[]) => {
    const yearRegex = /^(2\d{3}|3000)$/;
    return tags.filter((tag) => !yearRegex.test(tag) && !slots.includes(tag));
};

const CreateForum = ({ allTags }: { allTags: string[] }) => {
    const [title, setTitle] = useState("");
    const [year, setYear] = useState("");
    const [slot, setSlot] = useState("");
    const [description, setDescription] = useState("");
    const [selectedTags, setSelectedTags] = useState<string[]>([]);
    const [filteredTags, setFilteredTags] = useState<string[]>([]);
    const [showDropdown, setShowDropdown] = useState(false);
    const { toast } = useToast();
    const forumId = "cly4bhnc0000df02z5tshuhx7";
    const [pending, startTransition] = useTransition();
    const [tagInput, setTagInput] = useState("");

    const router = useRouter();
    const dropdownRef = useRef<HTMLDivElement>(null);

    const availableTags = useMemo(() => {
        return filterYearAndSlot(allTags);
    }, [allTags]);

    const fuse = useMemo(
        () =>
            new Fuse(availableTags, {
                threshold: 0.6,
                minMatchCharLength: 2,
            }),
        [availableTags]
    );

    useEffect(() => {
        setShowDropdown(false);
        if (tagInput) {
            const results = fuse.search(tagInput);
            setFilteredTags(results.map((result) => result.item));
        } else {
            setFilteredTags(availableTags);
        }
    }, [fuse, tagInput]);

    const handleTagInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setTagInput(e.target.value);
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        startTransition(async () => {
            const result = await createForumPost({
                title,
                forumId,
                description,
                year,
                slot,
                selectedTags,
            });
            if (result.success) {
                toast({
                    title: `Successfully posted: "${result.data?.title}"`,
                });
                router.push("/forum");
            } else {
                toast({
                    title: "Error: Could not post.",
                    variant: "destructive",
                });
                console.error("Error: ", result.error);
            }
        });
    };

    const handleTagSelect = (tag: string) => {
        if (!selectedTags.includes(tag)) {
            setSelectedTags([...selectedTags, tag]);
        }
        setTagInput("");
        setShowDropdown(false);
    };

    const handleKeyDown = (e: React.KeyboardEvent) => {
        if (e.key === "Enter") {
            e.preventDefault();
            if (!filteredTags.length) return;
            handleTagSelect(filteredTags[0]);
        }
    };

    const handleRemoveTag = (tag: string) => {
        setSelectedTags(selectedTags.filter((t) => t !== tag));
    };

    useEffect(() => {
        const handleClickOutside = (event: MouseEvent) => {
            if (
                dropdownRef.current &&
                !dropdownRef.current.contains(event.target as Node)
            ) {
                setShowDropdown(false);
            }
        };

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, []);

    return (
        <div className="flex justify-center items-center min-h-screen text-black dark:text-[#D5D5D5]">
            {pending && <Loading />}
            <div className="bg-white dark:bg-[#0C1222] p-6 shadow-lg w-full max-w-md border-dashed border-2 border-[#D5D5D5]">
                <div className="flex justify-between items-center mb-4">
                    <Link href={"/forum"}>
                        <button className="text-[#3BF3C7] px-2 py-2 border-2 border-[#3BF3C7] flex items-center justify-center font-bold hover:bg-[#ffffff]/10">
                            <FontAwesomeIcon icon={faArrowLeft} />
                        </button>
                    </Link>
                    <h3>New Post</h3>
                    <div className="relative group">
                        <div className="absolute inset-0 bg-black dark:bg-[#3BF4C7]" />
                        <div className="dark:absolute dark:inset-0 dark:blur-[75px] dark:lg:bg-none lg:dark:group-hover:bg-[#3BF4C7] transition dark:group-hover:duration-200 duration-1000" />
                        <button
                            type="submit"
                            onClick={handleSubmit}
                            className="dark:text-[#D5D5D5] dark:group-hover:text-[#3BF4C7] dark:group-hover:border-[#3BF4C7] dark:border-[#D5D5D5] dark:bg-[#0C1222] border-black border-2 relative px-4 py-2 text-lg bg-[#3BF4C7] text-black font-bold group-hover:-translate-x-1 group-hover:-translate-y-1 transition duration-150"
                        >
                            Post
                        </button>
                    </div>
                </div>
                <form onSubmit={handleSubmit} className="w-full">
                    <div className="mb-4">
                        <input
                            type="text"
                            placeholder="Title"
                            className={`p-2 border-2 border-dashed dark:bg-[#0C1222] border-gray-300 w-full text-black dark:text-[#D5D5D5] text-lg font-bold`}
                            value={title}
                            onChange={(e) => setTitle(e.target.value)}
                            required
                        />
                    </div>
                    <div className="grid grid-cols-2 gap-4 mb-4 place-content-center">
                        <div>
                            <select
                                className="p-2 w-full bg-[#5FC4E7] dark:bg-[#008A90] cursor-pointer transition-colors duration-300 hover:bg-opacity-85"
                                value={year}
                                onChange={(e) => setYear(e.target.value)}
                                required
                            >
                                <option value="">Year</option>
                                {years.map((y) => (
                                    <option key={y} value={y}>
                                        {y}
                                    </option>
                                ))}
                            </select>
                        </div>
                        <div>
                            <select
                                className="p-2 w-full bg-[#5FC4E7] dark:bg-[#008A90] cursor-pointer transition-colors duration-300 hover:bg-opacity-85"
                                value={slot}
                                onChange={(e) => setSlot(e.target.value)}
                                required
                            >
                                <option value="">Slot</option>
                                {slots.map((s) => (
                                    <option key={s} value={s}>
                                        {s}
                                    </option>
                                ))}
                            </select>
                        </div>
                    </div>
                    <div className="mb-4">
                        <textarea
                            placeholder="Thread Description"
                            className={`p-2 border-2 border-dashed border-gray-300 bg-white dark:bg-[#0C1222] w-full text-sm min-h-[150px]`}
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                            required
                        />
                    </div>
                    <div className="mb-4">
                        <div className="flex items-center mb-2 flex-wrap">
                            {selectedTags.map((tag) => (
                                <span
                                    key={tag}
                                    className="inline-block bg-white dark:bg-[#3F4451] px-3 py-1 text-xs font-semibold mr-2 mb-2"
                                >
                                    #{tag}
                                    <button
                                        type="button"
                                        onClick={() => handleRemoveTag(tag)}
                                        className="ml-2 text-red-500"
                                    >
                                        <FontAwesomeIcon icon={faSquareXmark} />
                                    </button>
                                </span>
                            ))}
                            <div className="relative w-full">
                                <input
                                    type="text"
                                    placeholder="Add tag"
                                    className={`p-2 border-2 border-dashed border-gray-300 w-full dark:bg-[#0C1222] text-lg font-bold`}
                                    value={tagInput}
                                    onChange={handleTagInputChange}
                                    onKeyDown={handleKeyDown}
                                    onFocus={() => setShowDropdown(true)}
                                />
                                {showDropdown && (
                                    <div
                                        ref={dropdownRef}
                                        className="absolute z-10 mt-1 w-full bg-white dark:bg-[#232530] shadow-lg max-h-60 py-1 text-base ring-1 ring-black ring-opacity-5 overflow-auto focus:outline-none sm:text-sm"
                                    >
                                        {filteredTags.map((tag) => (
                                            <div
                                                key={tag}
                                                className="cursor-pointer select-none relative py-2 pl-3 pr-9 hover:bg-[#ffffff]/10"
                                                onClick={() =>
                                                    handleTagSelect(tag)
                                                }
                                            >
                                                {tag}
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default CreateForum;
